import React, { useEffect, useState } from 'react';
import {
  AppBar,
  Box,
  Button,
  Card,
  CardContent,
  CardMedia,
  Container,
  Divider,
  Grid,
  IconButton,
  Stack,
  Toolbar,
  Typography,
} from "@mui/material";
import MenuIcon from "@mui/icons-material/Menu";
import Leftbar from '../component/Leftbar';

import AddIcon from "@mui/icons-material/Add";
import CreateMemory from "../component/PostForm";
import DeleteIcon from "@mui/icons-material/Delete";
import EditNoteIcon from "@mui/icons-material/EditNote";
import { Navigate, useNavigate } from "react-router-dom";
import axios from "axios";

const Home = () => {
  const token = localStorage.getItem("token");
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true); 


  const handlePost = (newPost) => {
    setPosts([newPost, ...posts]);
  };
  const [likes, setLikes] = useState(Array(posts.length).fill(false));

  const toggleLike = (index) => {
    const updatedLikes = [...likes];
    updatedLikes[index] = !updatedLikes[index];
    setLikes(updatedLikes);
  };

  const handleDoubleClick = (index) => {
    const updatedLikes = [...likes];
    updatedLikes[index] = true;
    setLikes(updatedLikes);
  };

  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);
  const handleClose = () => {
    setAnchorEl(null);
  };
  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleDelete = (postId) => {
    console.log(postId)

    axios.request({
        method: "delete",
        url: `http://16.170.173.197/posts/${postId}`,
        data: {
          id: postId,
        },
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then(() => {
        const updatedPosts = posts.filter((post) => {
          return post.id !== postId;
        });
        setPosts(updatedPosts);
      })
      .catch((error) => {
        console.error("Error deleting post:", error);
      });
  };
  const handleEditPost = (postId) => {
    const newDiscraption = prompt("please add the new disc");

    axios
      .request({
        method: "put",
        url: `http://16.170.173.197/posts/${postId}`,
        data: {
           posts:{
            description: newDiscraption,

        }
        },
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((response) => {
        console.log(response);
      })
      .catch((error) => {
        console.error("Error deleting post:", error);
      });
  };

  useEffect(() => {
    axios.get("http://16.170.173.197/posts", {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
      .then((response) => {
         setPosts(response.data.posts)
         setLoading(false);

      })
      .catch((error) => {
        console.error("Error:", error);
        setLoading(false);

      });
  }, [token]);



    return (
      <>
              <Leftbar onPost={handlePost} />

        <div>

              { posts.slice().reverse().map((memory, index) => (
                <Grid item xs={12} sm={6} md={4} lg={3} key={index}>
                  <Card key={index} className="post" sx={{ color: 'white', backgroundColor: 'black' }}>
          <div className="post__header">

          </div>

                    <Stack direction="row" spacing={1}>
                      <Button
                        onClick={() => handleDelete(memory.id)}
                        sx={{
                          display: "flex",
                          justifyContent: "center",
                          alignItems: "center",
                          color: "red",
                        }}
                      >
                        <DeleteIcon />
                      </Button>
                      <Button
                        onClick={() => handleEditPost(memory.id)}
                        sx={{
                          display: "flex",
                          justifyContent: "center",
                          alignItems: "center",
                          color: "blue",
                        }}
                      >
                        <EditNoteIcon />
                      </Button>
                    </Stack>
                    <div style={{ position: "relative" }}>
                      <div
                        style={{
                          margin: "10px",
                          position: "absolute",
                          zIndex: 1,
                          backgroundColor: "white",
                          borderRadius: "5px",
                          top: 0,
                          right: 0,
                          display: "flex",
                          justifyContent: "center",
                        }}
                      ></div>
                      <div>
                        <CardMedia
                          component="img"
                          image={memory.image}
                          sx={{
                            width: "100%",
                            height: "300px",
                            objectFit: "contain",
                          }}
                          loading="lazy"
                          alt=""
                        />
                      </div>
                    </div>
                    <CardContent>
                      <Typography style={{ fontFamily: "Signika" }}>
                        {memory.description}
                      </Typography>
                    </CardContent>
                    <Divider inset="context" />
                  </Card>
                </Grid>
              ))}
        </div>
      </>
    );
  };

export default Home;
