  setShowPostForm(false); // Hide the PostForm after posting
};

// In your Leftbar component where you render the PostForm
<PostForm onPost={handlePost} />


  return (
    <div>
      <form onSubmit={handleSubmit}>
        <TextField
          label="caption"
          variant="outlined"
          fullWidth
          value={caption}
          onChange={(e) => setcaption(e.target.value)}
        />
        <TextareaAutosize
          placeholder="Post body"
          minRows={3}
          maxRows={5}
          value={body}
          onChange={(e) => setBody(e.target.value)}
        />
        <TextField
          label="Image URL"
          variant="outlined"
          fullWidth
          value={url}
          onChange={(e) => setUrl(e.target.value)}
        />
              <Link id="but" to="/Home">

        <Button type="submit" variant="contained" color="primary">
          Post
        </Button>
        </Link>
        <Button variant="contained" color="secondary" onClick={onCancel}>
          Cancel
        </Button>
      </form>
    </div>
  );
};

export default PostForm;
