// StoryBar.js
import React from 'react';
import { Avatar, Box, Typography } from '@mui/material';

import boy from './assets/Avatars/boy.png';
import man from './assets/Avatars/man.png';
import nurse from './assets/Avatars/nurse.png';
import assistant from './assets/Avatars/shop-assistant.png';
import singer from './assets/Avatars/singer.png';
import steward from './assets/Avatars/steward.png';

const stories = [
  { imageUrl: boy, name: 'Boy', unread: true },
  { imageUrl: man, name: 'Man', unread: false },
  { imageUrl: nurse, name: 'Nurse', unread: false },
  { imageUrl: assistant, name: 'Assistant', unread: true },
  { imageUrl: singer, name: 'Singer', unread: false },
  { imageUrl: steward, name: 'Steward', unread: false },
  // Add more story objects as needed
];

const StoryBar = () => {
  return (
    <Box
      sx={{
        display: 'flex',
        overflowX: 'auto',
        p: 1,
        bgcolor: '#fff',
        borderBottom: '1px solid #ddd',
        borderRadius: '50px', // Adds a circular frame around all pictures
        borderWidth: 4, // Width of the frame
        borderStyle: 'solid', // Border style
        background: 'transparent', // Make the background transparent
        // Use a gradient border color to achieve mixed colors
        borderColor: 'linear-gradient(45deg, #FF5733, #33FF57, #5733FF, #FF5733, #33FF57, #5733FF)',
      }}
    >
      {stories.map((story, index) => (
        <Box key={index} sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginLeft: 2 }}>
          <Avatar
            sx={{
              width: 80,
              height: 80,
              marginBottom: 1,
              backgroundColor: 'transparent', // No background color for the circular frame
            }}
            alt={`Story ${index + 1}`}
            src={story.imageUrl}
          />
          <Typography variant="body2">{story.name}</Typography>
        </Box>
      ))}
    </Box>
  );
};

export default StoryBar;
