import React, { useState } from 'react';
// ... (other imports)

const Home = () => {
  const [likes, setLikes] = useState(Array(posts.length).fill(false));
  const [showPostForm, setShowPostForm] = useState(false); // State to track PostForm visibility

  // ... (other code)

  const handlePostClick = () => {
    // Show/hide the PostForm when the button is clicked
    setShowPostForm(!showPostForm);
  };

  // ... (other code)

  return (
    <div className='dd'>
      <StoryBar />
      <Rightbar />
      <button onClick={handlePostClick}>Create Post</button> {/* Button to toggle PostForm */}
      {showPostForm && (
        <PostForm onPost={handlePost} /> // Display PostForm when showPostForm is true
      )}
      {posts.map((post, index) => (
        <Post
          key={index}
          username={post.username}
          title={post.title}
          caption={post.caption}
          imageUrl={post.imageUrl}
          index={index}
        />
      ))}
    </div>
  );
};

export default Home;
