const Home = () => {
  const [posts, setPosts] = useState([]);
  const [usernames, setUsernames] = useState({});

  // ... (other functions and state variables)

  useEffect(() => {
    // Fetch the posts
    axios.get("http://16.170.173.197/posts", {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
      .then((response) => {
        setPosts(response.data.posts);

        // Create a set of unique user IDs from the posts
        const userIds = new Set(response.data.posts.map((post) => post.userId));

        // Fetch the usernames for each user ID
        userIds.forEach((userId) => {
          axios.get(`http://16.170.173.197/users/${userId}`)
            .then((userResponse) => {
              // Add the username to the usernames state variable
              setUsernames((prevUsernames) => ({
                ...prevUsernames,
                [userId]: userResponse.data.username,
              }));
            })
            .catch((error) => {
              console.error(`Error fetching username for user ${userId}:`, error);
            });
        });
      })
      .catch((error) => {
        console.error("Error fetching posts:", error);
      });
