import React, { useEffect, useState } from 'react';
import Button from '@mui/material/Button';
import axios from 'axios';


const token = localStorage.getItem("token");
  const name=localStorage.getItem("name")
  const avatar=localStorage.getItem("avatar")


function PostForm({ onPost }) {

  const [image, setImage] = useState(null)
  const [title, setTitle] = useState(null)
  const [description, setDescription] = useState(null)



  const formData=new formData();
  formData.append("image",image)
  formData.append("title",title)
  formData.append("description",description)



  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post("http://16.170.173.197/posts",formData,{
      headers: {
      Authorization: `Bearer ${token}`,
      "Content-Type":"multipart/form-data"
    },
  })} 








  const handleFileUpload = (input) => {
    const file = input.files[0]; 
    setImage(file);
    if (file) {
      const image = URL.createObjectURL(file);


    }
  };




  return (
    <div className="post-form">
      <h2>Create a New Post</h2>
      <form onSubmit={handleSubmit}>
        <div >
          <label htmlFor="title">Title:</label>
          <input
            type="text"
            id="title"
            name="title"
            value={title}
            onChange={(e)=>{
              setTitle({...title,title:e.target.value})}}
          />
        </div>
        <div >
          <label htmlFor="description">description:</label>
          <input
            id="description"
            name="description"
            value={description}
            onChange={(e)=>{
              setDescription({...description,description:e.target.value})}}          />
        </div>
        <div>
  <br />
  <input
    type="file"
    id="imageUpload"
    name="imageUpload"
    accept="image/*"

    onChange={(e) => handleFileUpload(e.target)}
    />
</div>

<Button type='submit' variant="contained" color="secondary" sx={{marginTop:4}}>Post</Button>

      </form>
    </div>
  );
}

export default PostForm;
