import React, { useState } from 'react';
import { Card, CardContent, Avatar, Typography, IconButton } from '@mui/material';
import FavoriteIcon from '@mui/icons-material/Favorite';
import ChatBubbleOutlineIcon from '@mui/icons-material/ChatBubbleOutline';
import SendIcon from '@mui/icons-material/Send';
import SaveIcon from '@mui/icons-material/Save';
import MoreHorizIcon from '@mui/icons-material/MoreHoriz';
import './style_home.css';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import StoryBar from '../component/Storybar';
import Rightbar from '../component/Rightbar';
import PostForm from '../component/PostForm'; // Import the PostForm component
import "../component/components.css";


const theme = createTheme({
  palette: {
    secondary: {
      main: '#FFFFFF',
    },
  },
});

const Home = () => {

  const toggleLike = (index) => {
    const updatedLikes = [...likes];
    updatedLikes[index] = !updatedLikes[index];
    setLikes(updatedLikes);
  };

  const handleDoubleClick = (index) => {
    const updatedLikes = [...likes];
    updatedLikes[index] = true; // Set liked status to true on double click
    setLikes(updatedLikes);
  };

  const handlePost = (newPost) => {
    // Add the new post to the beginning of the posts array
    posts.unshift(newPost);
    setLikes([false, ...likes]); // Add a like status for the new post
    setShowPostForm(false); // Hide the PostForm after posting
  };

  return (
    <div className='dd'>
      <StoryBar />
      <Rightbar />

      {posts.map((post, index) => (
        <Card key={index} className="post" sx={{ color: 'white', backgroundColor: 'black' }}>
          <div className="post__header">
            <IconButton className="post__dots" color="white">
              <MoreHorizIcon />
            </IconButton>
          </div>
          <div className="post__content">
            <div className="post__user-info">
              <Avatar
                className="post__avatar"
                alt={post.username}
                src="/static/images/avatar/1.jpg"
              />
              <div className="post__username">
                <Typography variant="subtitle2" color="white">{post.username}</Typography>
              </div>
            </div>
            <img
              className="post__image"
              src={post.imageUrl}
              alt={post.caption}
              onDoubleClick={() => handleDoubleClick(index)}
            />
          </div>
          <CardContent>
            <div className="post__buttons">
              <IconButton color={likes[index] ? 'error' : 'primary'} onClick={() => toggleLike(index)}>
                <FavoriteIcon color="white" />
              </IconButton>
              <IconButton color='error'>
                <ChatBubbleOutlineIcon color="white" />
              </IconButton>
              <IconButton color="secondary">
                <SendIcon color="white" />
              </IconButton>
              <IconButton color="error" className="post__save-icon">
                <SaveIcon color="white" />
              </IconButton>
            </div>
            {likes[index] && (
              <Typography variant="body2" color="error">
                1 like
              </Typography>
            )}
            <Typography variant="body2">
              <strong>{post.username}</strong> {post.caption}
            </Typography>
          </CardContent>
        </Card>
      ))}
    </div>
  );
};

export default Home;
