import React, { useState } from 'react';
// Import other necessary components and styles

function Leftbar({ onPost }) {
  const [showPostForm, setShowPostForm] = useState(false);
  const [newPost, setNewPost] = useState({ title: '', caption: '', imageUrl: '' });

  const handlePost = () => {
    onPost(newPost);
    setNewPost({ title: '', caption: '', imageUrl: '' });
    setShowPostForm(false);
  };

  return (
    <div className='leftbar'>
      {/* Your Leftbar content */}
      <ListItem>
        <ListItemText>
          <Button variant="contained" color="primary" onClick={() => setShowPostForm(!showPostForm)}>
            Create Post
          </Button>
        </ListItemText>
      </ListItem>

      {showPostForm && (
        <PostForm
          newPost={newPost}
          setNewPost={setNewPost}
          onPost={handlePost}
        />
      )}

      {/* Other Leftbar content */}
    </div>
  );
}

export default Leftbar;
