import React, { useEffect, useState } from 'react';
import {
  Card,
  CardContent,
  Avatar,
  Typography,
  IconButton,
  Menu,
  MenuItem,
  Stack,
  Skeleton,
} from '@mui/material';
import FavoriteBorderOutlinedIcon from '@mui/icons-material/FavoriteBorderOutlined';
import ChatBubbleOutlineIcon from '@mui/icons-material/ChatBubbleOutline';
import SendIcon from '@mui/icons-material/Send';
import MoreHorizIcon from '@mui/icons-material/MoreHoriz';
import './Pagesstyle.css';
import StoryBar from '../component/Storybar';
import Rightbar from '../component/Rightbar';
import Leftbar from '../component/Leftbar';
import axios from 'axios';
import BookmarkBorderOutlinedIcon from '@mui/icons-material/BookmarkBorderOutlined';

const Home = () => {
  const token = localStorage.getItem("token");
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [likes, setLikes] = useState([]);

  useEffect(() => {
    axios.get("http://16.170.173.197/posts", { headers: { Authorization: `Bearer ${token}` } })
      .then((response) => {
        const receivedPosts = response.data.posts;
        setPosts(receivedPosts);
        setLikes(new Array(receivedPosts.length).fill(false));
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error:", error);
        setLoading(false);
      });
  }, [token]);

  const handleDelete = (postId) => {
    axios.delete(`http://16.170.173.197/posts/${postId}`, { headers: { Authorization: `Bearer ${token}` } })
      .then(() => setPosts(posts.filter((post) => post.id !== postId)))
      .catch((error) => console.error("Error deleting post:", error));
  };

  const handleEdit = (postId) => {
    const newDescription = prompt("Please add the new description");
    if (newDescription !== null) {
      axios.put(`http://16.170.173.197/posts/${postId}`, { description: newDescription }, { headers: { Authorization: `Bearer ${token}` } })
        .then((response) => console.log(response))
        .catch((error) => console.error("Error editing post:", error));
    }
  };

  const handlePost = (newPost) => setPosts([newPost, ...posts]);

  const toggleLikePost = (postId, isLiked) => {
    isLiked ? unlikePost(postId) : likePost(postId);
  };

  const likePost = (postId, index) => {
    axios.post(`http://16.170.173.197/posts/like/${postId}`, null, { headers: { Authorization: `Bearer ${token}` } })
      .then((response) => setPosts(posts.map((post, i) => (i === index ? response.data.post : post)))
      .catch((error) => console.error("Error liking post:", error)));
  };

  const unlikePost = (postId) => {
    axios.delete(`http://16.170.173.197/posts/like/${postId}`, { headers: { Authorization: `Bearer ${token}` } })
      .then(() => setLikes(likes.filter((likeId) => likeId !== postId)))
      .catch((error) => console.error("Error unliking post:", error));
  };

  const isLiked = (postId) => likes.includes(postId);

  const handleDoubleClick = (index) => toggleLikePost(posts[index].id, isLiked(posts[index].id));
  return (

    <div className="dd">

      <StoryBar />
      <Rightbar />
      <Leftbar onPost={handlePost} />
      {loading ? (
        <Stack spacing={2} className="post" >
      <Skeleton variant="circular"  width={50} height={50} sx={{backgroundColor:"gray"}} />
      <Skeleton variant="rectangular" width={"40vw"} height={"40vw"}sx={{backgroundColor:"gray"}}  />
      <Skeleton variant="rounded" width={"40vw"} height={40} />
      <Skeleton variant="circular"  width={50} height={50} sx={{backgroundColor:"gray"}} />
      <Skeleton variant="rectangular" width={"40vw"} height={"40vw"}sx={{backgroundColor:"gray"}}  />
      <Skeleton variant="rounded" width={"40vw"} height={40} />
    </Stack>        
        ) : (
      posts.slice().reverse().map((post, index) => (

        <Card key={index} className="post" sx={{ color: 'white', backgroundColor: 'black' }}>

            <PopupState variant="popover" popupId="demo-popup-menu">
      {(popupState) => (
        <React.Fragment>
                    <IconButton color='primary' {...bindTrigger(popupState)}sx={{marginLeft:"35vw",marginBottom:"-3vw"}}>
              <MoreHorizIcon  />
            </IconButton>

          <Menu {...bindMenu(popupState)}>
            <MenuItem onClick={()=>handleDelete(post.id)} sx={{color:"red"}}>Delete Post</MenuItem>
            <MenuItem onClick={()=>handleEdit(post.id)} sx={{color:"blue"}}>Edit Post</MenuItem>


          </Menu>
        </React.Fragment>
      )}
    </PopupState>

          <div className="post__content">
            <div className="post__user-info">
              <Avatar
                className="post__avatar"
                alt={post.description}
                src={post.user ? post.user.avatar : 'Unknown avatar'}
              />
              <div className="post__username">
              <Typography variant="subtitle2">{post.user ? post.user.userName : 'Unknown User'}</Typography>
              </div>
            </div>
            <img
              className="post__image"
              src={post.image}
              alt={post.description}
              onDoubleClick={() => handleDoubleClick(index)}
            />
          </div>
          <CardContent>
            <div className="post__buttons">
            <IconButton color="primary"onClick={() => toggleLikePost(post.id, isLiked(post.id))}>
            <FavoriteBorderOutlinedIcon fontSize='medium' />
            </IconButton>


              <IconButton color="primary">
                <ChatBubbleOutlineIcon  fontSize='medium'/>
              </IconButton>
              <IconButton color="primary">
                <SendIcon  fontSize='medium'/>
              </IconButton>
              <IconButton color="primary" sx={{marginLeft:"29vw"}} className="post__save-icon">
                <BookmarkBorderOutlinedIcon fontSize='medium' />
              </IconButton>
            </div>
            <Typography variant="body2">{post.likes ? `${post.likes.length} likes` : '0 likes'}</Typography>
            <Typography variant="body2" sx={{display:"flex"}}>
              <strong>{post.user ? post.user.userName : 'Unknown User'}</strong> <span style={{ marginLeft: "1vw",width:"30vw" }}> {post.description}</span> 
            </Typography>
          </CardContent>

        </Card>

      ))
      )}
    </div>
  );}

export default Home;
