// StoryBar.js
import React, { useState } from 'react';
import { Avatar, Box, Typography } from '@mui/material';

import boy from './assets/Avatars/boy.png';
import man from './assets/Avatars/man.png';
import nurse from './assets/Avatars/nurse.png';
import assistant from './assets/Avatars/shop-assistant.png';
import singer from './assets/Avatars/singer.png';
import steward from './assets/Avatars/steward.png';
import './components.css'; // Import your CSS file for styling

const stories = [
  { imageUrl: boy, name: 'Boy', unread: true },
  { imageUrl: man, name: 'Man', unread: false },
  { imageUrl: nurse, name: 'Nurse', unread: false },
  { imageUrl: assistant, name: 'Assistant', unread: true },
  { imageUrl: singer, name: 'Singer', unread: false },
  { imageUrl: steward, name: 'Steward', unread: false },
  // Add more story objects as needed
];

const StoryBar = () => {
  const [slideIndex, setSlideIndex] = useState(0);

  const handleSlideChange = (newSlideIndex) => {
    setSlideIndex(newSlideIndex);
  };

  return (
    <div className="Storybar">
      <div className="Slider" style={{ transform: `translateX(-${slideIndex * 100}%)` }}>
        {stories.map((story, index) => (
          <div key={index} className="StorySlide">
            <div className="CircularFrame">
              <Avatar
                sx={{
                  width: 80,
                  height: 80,
                  borderRadius: '50%',
                  backgroundColor: 'black', // Make the avatar circular
                }}
                alt={`Story ${index + 1}`}
                src={story.imageUrl}
              />
            </div>
            <Typography variant="body2">{story.name}</Typography>
          </div>
        ))}
      </div>
      <div className="SliderControls">
        <button onClick={() => handleSlideChange(Math.max(slideIndex - 1, 0))}>&lt;</button>
        <button onClick={() => handleSlideChange(Math.min(slideIndex + 1, stories.length - 1))}>&gt;</button>
      </div>
    </div>
  );
};

export default StoryBar;
