import React, { useState } from 'react';
import { Card, CardContent, Avatar, Typography, IconButton } from '@mui/material';
import FavoriteIcon from '@mui/icons-material/Favorite';
import ChatBubbleOutlineIcon from '@mui/icons-material/ChatBubbleOutline';
import SendIcon from '@mui/icons-material/Send';
import SaveIcon from '@mui/icons-material/Save';
import MoreHorizIcon from '@mui/icons-material/MoreHoriz';
import './style_home.css';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import StoryBar from '../component/Storybar';
import Rightbar from '../component/Rightbar';
import PostForm from '.PostForm'; // Import the PostForm component
import Leftbar from '../component/Leftbar';
import "../component/components.css";

// Default posts
const defaultPosts = [
  {
    username: 'user1',
    title: 'Title 1',
    caption: 'Caption 1',
    imageUrl: 'https://car-images.bauersecure.com/wp-images/13349/1056x594/80-mercedes-amg-gt63-e-performance-hero.jpg',
  },
  {
    username: 'user2',
    title: 'Title 2',
    caption: 'Caption 2',
    imageUrl: 'https://via.placeholder.com/500x500',
  },
  // Add more default post objects here
];

const theme = createTheme({
  palette: {
    secondary: {
      main: '#FFFFFF',
    },
  },
});

const Home = () => {
  const [likes, setLikes] = useState(Array(defaultPosts.length).fill(false));
  const [showPostForm, setShowPostForm] = useState(false);
  const [posts, setPosts] = useState(defaultPosts); // Use state to manage posts

  const toggleLike = (index) => {
    const updatedLikes = [...likes];
    updatedLikes[index] = !updatedLikes[index];
    setLikes(updatedLikes);
  };

  const handleDoubleClick = (index) => {
    const updatedLikes = [...likes];
    updatedLikes[index] = true;
    setLikes(updatedLikes);
  };

  const handlePost = (newPost) => {
    const updatedPosts = [newPost, ...posts]; // Add the new post to the beginning of the posts array
    setPosts(updatedPosts);
    setLikes([false, ...likes]);
    setShowPostForm(false);
  };

  return (
    <div className='dd'>
      <StoryBar />
      <Leftbar onPostButtonClick={() => setShowPostForm(true)} />
      <Rightbar />
      {showPostForm && (
        <PostForm onPost={handlePost} />
      )}
      {posts.map((post, index) => (
        <Card key={index} className="post" sx={{ color: 'white', backgroundColor: 'black' }}>
          {/* ... (rest of the post rendering code) */}
        </Card>
      ))}
    </div>
  );
};

export default Home;
