import React, { useState } from 'react';
import {
  Card,
  CardContent,
  Avatar,
  Typography,
  IconButton,
  Button,
} from '@mui/material';
import FavoriteIcon from '@mui/icons-material/Favorite';
import ChatBubbleOutlineIcon from '@mui/icons-material/ChatBubbleOutline';
import SendIcon from '@mui/icons-material/Send';
import SaveIcon from '@mui/icons-material/Save';
import MoreHorizIcon from '@mui/icons-material/MoreHoriz';

const Home = () => {
  // Sample data for posts
  const [posts, setPosts] = useState([
    {
      id: 1,
      username: 'User 1',
      caption: 'Hello, how are you?',
      imageUrl: '/path/to/image1.jpg',
      likes: 5,
    },
    {
      id: 2,
      username: 'User 2',
      caption: 'Hi there!',
      imageUrl: '/path/to/image2.jpg',
      likes: 3,
    },
    // Add more posts here
  ]);

  // Function to handle liking a post
  const handleLike = (postId) => {
    // Find the post by postId and update its likes count
    const updatedPosts = posts.map((post) =>
      post.id === postId ? { ...post, likes: post.likes + 1 } : post
    );
    setPosts(updatedPosts);
  };

  // Function to delete a post
  const handleDelete = (postId) => {
    // Remove the post with the specified postId from the posts array
    const updatedPosts = posts.filter((post) => post.id !== postId);
    setPosts(updatedPosts);
  };

  return (
    <div>
      {/* Render the static story section */}
      {/* You can design this section with images and captions */}

      {/* Render the dynamic posts */}
      {posts.map((post) => (
        <Card key={post.id}>
          {/* Post header with more options */}
          <div className="post__header">
            <IconButton color="primary">
              <MoreHorizIcon />
            </IconButton>
          </div>

          {/* User info and post image */}
          <div className="post__content">
            <div className="post__user-info">
              <Avatar alt={post.username} src="/path/to/avatar.jpg" />
              <div className="post__username">
                <Typography variant="subtitle2">{post.username}</Typography>
              </div>
            </div>
            <img
              className="post__image"
              src={post.imageUrl}
              alt={post.caption}
            />
          </div>

          {/* Like, comment, send, and save buttons */}
          <CardContent>
            <div className="post__buttons">
              <IconButton color="primary" onClick={() => handleLike(post.id)}>
                <FavoriteIcon />
              </IconButton>
              <IconButton color="primary">
                <ChatBubbleOutlineIcon />
              </IconButton>
              <IconButton color="primary">
                <SendIcon />
              </IconButton>
              <IconButton color="primary">
                <SaveIcon />
              </IconButton>
              <Button
                color="error"
                onClick={() => handleDelete(post.id)}
              >
                Delete
              </Button>
            </div>
            <Typography variant="body2">
              <strong>{post.username}</strong> {post.caption}
            </Typography>
          </CardContent>
        </Card>
      ))}

      {/* Button to create new posts */}
      <Button
        variant="contained"
        color="primary"
        onClick={() => {
          // Implement a function or component to create new posts
        }}
      >
        Create New Post
      </Button>
    </div>
  );
};

export default Home;
