import React, { useState } from 'react';
import Button from '@mui/material/Button';

function PostForm({ onPost }) {
  const [formData, setFormData] = useState({
    title: '',
    caption: '',
    imageUrl: '',
  });
  const [isFormVisible, setFormVisible] = useState(true);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const newPost = {
      username: 'Ezz_kalash',
      title: formData.title,
      caption: formData.caption,
      imageUrl: formData.imageUrl,
    };

    onPost(newPost);

    setFormData({
      title: '',
      caption: '',
      imageUrl: '',
    });
  };

  function handleFileUpload(input) {
    const selectedFile = input.files[0]; // Get the selected file

    if (selectedFile) {
      const selectedImageElement = document.getElementById('selectedImage');

      // Create a FileReader to read the selected image file
      const reader = new FileReader();

      // Set up an event handler to run when the FileReader finishes reading the file
      reader.onload = function (e) {
        const imageUrl = e.target.result; // Get the base64-encoded image URL
        selectedImageElement.src = imageUrl; // Set the image source
        selectedImageElement.style.display = 'block'; // Display the image
      };

      // Read the selected image file as a data URL
      reader.readAsDataURL(selectedFile);
    }
  }

  const handleExitClick = () => {
    setFormVisible(false);
  };

  return (
    <div className={`post-form ${isFormVisible ? 'visible' : 'hidden'}`}>
      <h2>Create a New Post</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="title">Title:</label>
          <input
            type="text"
            id="title"
            name="title"
            value={formData.title}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="caption">Caption:</label>
          <textarea
            id="caption"
            name="caption"
            value={formData.caption}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="imageUpload">Choose File:</label>
          <input
            type="file"
            id="imageUpload"
            name="imageUpload"
            accept="image/*"
            onChange={handleFileUpload}
          />
          <img
            id="selectedImage"
            src=""
            alt="Selected"
            style={{ display: 'none' }}
          />
        </div>

        <Button type="submit" variant="contained" color="secondary">
          Post
        </Button>

        <Button
          variant="contained"
          color="error"
          onClick={handleExitClick}
        >
          Exit
        </Button>
      </form>
    </div>
  );
}

export default PostForm;
