import React, { useState } from 'react';
import { Card, CardContent, Avatar, Typography, IconButton } from '@mui/material';
import FavoriteIcon from '@mui/icons-material/Favorite';
import ChatBubbleOutlineIcon from '@mui/icons-material/ChatBubbleOutline';
import SendIcon from '@mui/icons-material/Send';
import SaveIcon from '@mui/icons-material/Save';
import MoreHorizIcon from '@mui/icons-material/MoreHoriz';
import './style_home.css'; 
import { createTheme, ThemeProvider } from '@mui/material/styles'; 
import StoryBar from '../component/Storybar';
import Rightbar from '../component/Rightbar';
import "../component/components.css"


const posts = [
  {
    username: 'user1',
    caption: 'Caption 1',
    imageUrl: 'https://via.placeholder.com/500x500',
  },
  {
    username: 'user2',
    caption: 'Caption 2',
    imageUrl: 'https://via.placeholder.com/500x500',
  },
  // Add more post objects here
];

const theme = createTheme({
  palette: {
    secondary: {
      main: '#FFFFFF', // White secondary color
    },
  },
});

const Home = () => {
  const [likes, setLikes] = useState(Array(posts.length).fill(false));

  const toggleLike = (index) => {
    const updatedLikes = [...likes];
    updatedLikes[index] = !updatedLikes[index];
    setLikes(updatedLikes);
  };

  const Post = ({ username, caption, imageUrl, index }) => {
    return (
      <Card className="post" sx={{ color: 'white', backgroundColor: 'black' }}>
        {/* Three Dots icon */}
        <div className="post__header">
          <IconButton className="post__dots">
            <MoreHorizIcon />
          </IconButton>
        </div>

        {/* User info and Post image */}
        <div className="post__content">
          <div className="post__user-info">
            <Avatar
              className="post__avatar"
              alt={username}
              src="/static/images/avatar/1.jpg" 
            />
            <div className="post__username">
              <Typography variant="subtitle2">{username}</Typography>
            </div>
          </div>
          <img className="post__image" src={imageUrl} alt={caption} />
        </div>

        {/* Like, Comment, Send, and Save buttons */}
        <CardContent>
          <div className="post__buttons">
            <IconButton color="primary" onClick={() => toggleLike(index)}>
              {likes[index] ? <FavoriteIcon color="error" /> : <FavoriteIcon />}
            </IconButton>
            <IconButton color='error'>
              <ChatBubbleOutlineIcon />
            </IconButton>
            <IconButton color="secondary">
              <SendIcon />
            </IconButton>
            <IconButton color="error" className="post__save-icon">
              <SaveIcon />
            </IconButton>
          </div>

          {likes[index] ? (
            <Typography variant="body2" color="error">
              {likes[index] ? '1 like' : ''}
            </Typography>
          ) : null}

          <Typography variant="body2">
            <strong>{username}</strong> {caption}
          </Typography>
        </CardContent>
      </Card>
    );
  };

  return (
    <div className='dd'>
      <StoryBar />
      <Rightbar />
      {posts.map((post, index) => (
        <Post
          key={index}
          username={post.username}
          caption={post.caption}
          imageUrl={post.imageUrl}
          index={index}
        />
      ))}
    </div>
  );
};

export default Home;
