// Import necessary components and libraries
import React, { useState } from 'react';
import {
  Card,
  CardContent,
  Avatar,
  Typography,
  IconButton,
} from '@mui/material';
import FavoriteIcon from '@mui/icons-material/Favorite';
import ChatBubbleOutlineIcon from '@mui/icons-material/ChatBubbleOutline';
import SendIcon from '@mui/icons-material/Send';
import SaveIcon from '@mui/icons-material/Save';
import MoreHorizIcon from '@mui/icons-material/MoreHoriz';
import './style_home.css';
import { createTheme } from '@mui/material/styles';
import StoryBar from '../component/Storybar';
import Rightbar from '../component/Rightbar';
import '../component/components.css';
import Post from '../component/Post'; // Import the Post component

const theme = createTheme({
  palette: {
    secondary: {
      main: '#FFFFFF',
    },
  },
});

const Home = ({ posts }) => {
  const [likes, setLikes] = useState(Array(posts.length).fill(false));

  const toggleLike = (index) => {
    const updatedLikes = [...likes];
    updatedLikes[index] = !updatedLikes[index];
    setLikes(updatedLikes);
  };

  const handleDoubleClick = (index) => {
    const updatedLikes = [...likes];
    updatedLikes[index] = true; // Set liked status to true on double click
    setLikes(updatedLikes);
  };

  return (
    <div className="dd">
      <StoryBar />
      <Rightbar />

      {/* Map through the posts including the default post */}
      {posts.map((post, index) => (
        <Post
          key={index}
          username={post.username}
          imageUrl={post.imageUrl}
          caption={post.caption}
          liked={likes[index]}
          onLike={() => toggleLike(index)}
        />
      ))}

      {/* Add your default post here */}
      <Post
        username="Default User"
        imageUrl="https://example.com/default-image.jpg"
        caption="This is a default post caption."
        liked={false}
        onLike={() => {}}
      />
    </div>
  );
};

export default Home;
