// StoryBar.js
import React from 'react';
import { Avatar, Box, Typography } from '@mui/material';
import Slider from 'react-slick'; // Import the Slider component
import 'slick-carousel/slick/slick.css'; // Import the slick-carousel styles
import 'slick-carousel/slick/slick-theme.css'; // Import the slick-carousel theme styles
import './components.css'; // Import your CSS file for styling

import boy from './assets/Avatars/boy.png';
import man from './assets/Avatars/man.png';
import nurse from './assets/Avatars/nurse.png';
import assistant from './assets/Avatars/shop-assistant.png';
import singer from './assets/Avatars/singer.png';
import steward from './assets/Avatars/steward.png';

const stories = [
  { imageUrl: boy, name: 'Boy', unread: true },
  { imageUrl: man, name: 'Man', unread: false },
  { imageUrl: nurse, name: 'Nurse', unread: false },
  { imageUrl: assistant, name: 'Assistant', unread: true },
  { imageUrl: singer, name: 'Singer', unread: false },
  { imageUrl: steward, name: 'ezz', unread: false },
  { imageUrl: nurse, name: 'dd', unread: false },
  { imageUrl: steward, name: 'dd', unread: false },
  { imageUrl: assistant, name: 'aa', unread: false },
  // Add more story objects as needed
];

const CircularFrame = ({ children }) => (
  <div
    style={{
      width: '88px', // Adjust the size as needed
      height: '88px', // Adjust the size as needed
      background: 'linear-gradient(45deg, #FF5733, #33FF57, #5733FF)',
      borderRadius: '50%',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
    }}
  >
    {children}
  </div>
);

const StoryBar = () => {
  const settings = {
    infinite: false,
    speed: 500,
    slidesToShow: 5,
    slidesToScroll: 5,
  };

  return (
    <div className='Storybar' style={{ background: 'black', maxWidth: '400px', margin: '0 auto' }}>
      <Slider {...settings}>
        {stories.map((story, index) => (
          <div key={index} style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginLeft: 2 }}>
            <CircularFrame>
              <Avatar
                sx={{
                  width: 80,
                  height: 80,
                  borderRadius: '50%',
                  backgroundColor: 'black',
                }}
                alt={`Story ${index + 1}`}
                src={story.imageUrl}
              />
            </CircularFrame>
            <Typography variant="body2">{story.name}</Typography>
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default StoryBar;
