import React, { useState } from 'react';
import instalogo from "./assets/instagram-logo.png";
import { Link } from "react-router-dom";
import ListItemButton from '@mui/material/ListItemButton';
import Button from '@mui/material/Button';
import HomeIcon from '@mui/icons-material/Home';
import SearchIcon from '@mui/icons-material/Search';
import ExploreIcon from '@mui/icons-material/Explore';
import SlideshowIcon from '@mui/icons-material/Slideshow';
import ChatIcon from '@mui/icons-material/Chat';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import MenuIcon from '@mui/icons-material/Menu';
import ListItem from '@mui/material/ListItem';
import ListItemText from '@mui/material/ListItemText';
import Avatar from '@mui/material/Avatar';
import boy from '../component/assets/Ezz.png';
import "./components.css";
import PostForm from "./PostForm"; // Import the PostForm component

export const posts = [
  {
    username: 'user1',
    title: 'Title 1',
    caption: 'Caption 1',
    imageUrl: 'https://car-images.bauersecure.com/wp-images/13349/1056x594/80-mercedes-amg-gt63-e-performance-hero.jpg',
  },
  {
    username: 'user2',
    title: 'Title 2',
    caption: 'Caption 2',
    imageUrl: 'https://via.placeholder.com/500x500',
  },
  // Add more post objects here
];

function Leftbar() {
  const [showPostForm, setShowPostForm] = useState(false); // State to track PostForm visibility

  return (
    <div className='leftbar'>
      <img id="insta" src={instalogo} alt="instalogo" width={"100px"} />
      <Link id="but" to="/">
        <ListItemButton>
          <HomeIcon fontSize='small' /> Home
        </ListItemButton>
      </Link>

      <Link to="/prog">
        <ListItemButton id="but" color='white'>
          <SearchIcon fontSize='small' /> Search
        </ListItemButton>
      </Link>

      <Link to="/exp">
        <ListItemButton id="but">
          <ExploreIcon fontSize='small' /> Explore
        </ListItemButton>
      </Link>

      <ListItemButton id="but">
        <SlideshowIcon fontSize='small' /> Reels
      </ListItemButton>

      <ListItemButton id="but">
        <ChatIcon fontSize='small' /> Messages
      </ListItemButton>

      <ListItemButton id="but">
        <FavoriteBorderIcon fontSize='small' /> Notification
      </ListItemButton>

      <ListItem>
        <ListItemText>
        <Button variant="contained" color="primary" onClick={() => setShowPostForm(!showPostForm)}>
            Create Post
          </Button>
        </ListItemText>
      </ListItem>

      {showPostForm && (
        <PostForm />
      )}

      <Link id="but" to="/prof">
        <ListItemButton id="but">
          <Avatar src={boy} />
          <ListItemText primary="Ezzkalash" secondary="ezzo" />
        </ListItemButton>
      </Link>

      <ListItemButton>
        <MenuIcon fontSize='small' /> Menu
      </ListItemButton>
    </div>
  );
}

export default Leftbar;
