import React, { useState } from 'react';
import PostForm from './PostForm'; // Import the PostForm component
import { Button, ListItem, ListItemButton } from '@mui/material';

export const posts = [
  // Your existing post objects here
];

function Leftbar() {
  const [likes, setLikes] = useState(Array(posts.length).fill(false));

  const [showPostForm, setShowPostForm] = useState(false); // State to track PostForm visibility

  const handlePost = (newPost) => {
    // Add the new post to the beginning of the posts array
    posts.unshift(newPost);
    setLikes([false, ...likes]); // Add a like status for the new post
    setShowPostForm(false); // Hide the PostForm after posting
  };

  return (
    <div className='leftbar'>
      {/* Your existing code */}
      <ListItem>
        <ListItemButton>
          <Button
            variant="contained"
            color="primary"
            onClick={() => setShowPostForm(!showPostForm)}
          >
            Create Post
          </Button>
        </ListItemButton>
      </ListItem>
      {showPostForm && <PostForm onPost={handlePost} />}
      {/* Your existing code */}
    </div>
  );
}

export default Leftbar;
