import React, { useEffect, useState } from 'react';
import Button from '@mui/material/Button';
import axios from 'axios';

function PostForm({ onPost }) {
  const [formData, setFormData] = useState({
    title: '',
    caption: '',
    image: null,
  });


  const handleSubmit = (e) => {
    e.preventDefault();
    const newPost = {
      username: 'Ezz_kalash',
      title: formData.title,
      caption: formData.caption,
      image: formData.imageUrl,
    };

    onPost(newPost);

    setFormData({
      title: '',
      caption: '',
      image: '',
    });
  };


  const token=localStorage.getItem("token")
  useEffect=()=>{
    axios.get("http://16.170.173.197/posts",{
      headers:{
        Authorization: `Bearer ${token}`
      }
    })
    .then((response)=>{
      console.log(response)
    })
  }


  const handleFileUpload = (input) => {
    const selectedFile = input.files[0]; // Get the selected file

    if (selectedFile) {
      const image = URL.createObjectURL(selectedFile);

      setFormData({
        ...formData,
        image: selectedFile,
      });
    }
  };




  return (
    <div className="post-form">
      <h2>Create a New Post</h2>
      <form onSubmit={handleSubmit}>
        <div >
          <label htmlFor="title">Title:</label>
          <input
            type="text"
            id="title"
            name="title"
            value={formData.title}
            onChange={(e)=>{
              setFormData({...formData,title:e.target.value})}}
          />
        </div>
        <div >
          <label htmlFor="caption">Caption:</label>
          <input
            id="caption"
            name="caption"
            value={formData.caption}
            onChange={(e)=>{
              setFormData({...formData,caption:e.target.value})}}          />
        </div>
        <div>
  <br />
  <input
    type="file"
    id="imageUpload"
    name="imageUpload"
    accept="image/*"

    onChange={(e) => handleFileUpload(e.target)}
    />
</div>

<Button type='submit' variant="contained" color="secondary" sx={{marginTop:4}}>Post</Button>

      </form>
    </div>
  );
}

export default PostForm;
