import React, { useEffect, useState } from 'react';
import {Card,CardContent,Avatar,Typography,IconButton} from '@mui/material';
import FavoriteBorderOutlinedIcon from '@mui/icons-material/FavoriteBorderOutlined';
import ChatBubbleOutlineIcon from '@mui/icons-material/ChatBubbleOutline';
import SendIcon from '@mui/icons-material/Send';
import MoreHorizIcon from '@mui/icons-material/MoreHoriz';
import './Pagesstyle.css';
import StoryBar from '../component/Storybar';
import Rightbar from '../component/Rightbar';
import Leftbar from '../component/Leftbar';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import axios from 'axios';
import BookmarkBorderOutlinedIcon from '@mui/icons-material/BookmarkBorderOutlined';

const Home = () => {
  const token = localStorage.getItem("token");
  const [posts, setPosts] = useState([]);

  const handlePost = (newPost) => {
    setPosts([newPost, ...posts]);
  };
  const [likes, setLikes] = useState(Array(posts.length).fill(false));

  const toggleLike = (index) => {
    const updatedLikes = [...likes];
    updatedLikes[index] = !updatedLikes[index];
    setLikes(updatedLikes);
  };

  const handleDoubleClick = (index) => {
    const updatedLikes = [...likes];
    updatedLikes[index] = true;
    setLikes(updatedLikes);
  };
  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);
  const handleClose = () => {
    setAnchorEl(null);
  };
  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };
  // function handleDelete(postid) {
  //   console.log(postid);

  //   axios.delete(`http://16.170.173.197/posts/${postid}`, {
  //     headers: {
  //       Authorization: `Bearer ${token}`, 
  //     },
  //   })
  //   .then((response) => {
  //     console.log('Post deleted successfully');
  //   })
  //   .catch((error) => {
  //     alert(`Error: ${error.message}`);

  //   });
  // }
  const handleDeletePost = (postId) => {
    axios
      .request({
        method: "delete",
        url: `http://16.170.173.197/posts/${postId}`,
        data: {
          id: postId,
        },
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((response) => {
        const updatedPosts = posts.filter((post) => {
          return post.id !== postId;
        });
        setP(updatedPosts);
      })
      .catch((error) => {
        console.error("Error deleting post:", error);
      });
  };

  useEffect(() => {
    axios.get("http://16.170.173.197/posts", {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
      .then((response) => {
        console.log(response);
         setPosts(response.data.posts)
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  }, []);

  return (
    <div className="dd">
      <StoryBar />
      <Rightbar />
      <Leftbar onPost={handlePost} />


      {posts.map((post, index) => (
        <Card key={index} className="post" sx={{ color: 'white', backgroundColor: 'black' }}>
          <div className="post__header">

          </div>
          <IconButton color='primary' sx={{marginLeft:67,marginBottom:-7}}
          aria-controls={open ? 'basic-menu' : undefined}
              aria-haspopup="true"
              aria-expanded={open ? 'true' : undefined}
              onClick={handleClick} >
              <MoreHorizIcon  />
            </IconButton>
            <Menu
        id="basic-menu"
        anchorEl={anchorEl}
        open={open}
        onClose={handleClose}
        MenuListProps={{
          'aria-labelledby': 'basic-button',
        }}
      >
        <MenuItem onClick={()=>handleDelete(post.id)} sx={{color:"red"}} >Delete Post</MenuItem>
      </Menu>
          <div className="post__content">
            <div className="post__user-info">
              <Avatar
                className="post__avatar"
                alt={post.description}
                src="/static/images/avatar/1.jpg"
              />
              <div className="post__username">
                <Typography variant="subtitle2" >{post.user.userName}</Typography>
              </div>
            </div>
            <img
              className="post__image"
              src={post.image}
              alt={post.description}
              onDoubleClick={() => handleDoubleClick(index)}
            />
          </div>
          <CardContent>
            <div className="post__buttons">
              <IconButton color={likes[index] ? 'error' : 'primary'} onClick={() => toggleLike(index)}>
                <FavoriteBorderOutlinedIcon fontSize='medium'  /> 
              </IconButton>
              <IconButton color="primary">
                <ChatBubbleOutlineIcon  fontSize='medium'/>
              </IconButton>
              <IconButton color="primary">
                <SendIcon  fontSize='medium'/>
              </IconButton>
              <IconButton color="primary" sx={{marginLeft:"29vw"}} className="post__save-icon">
                <BookmarkBorderOutlinedIcon fontSize='medium' />
              </IconButton>
            </div>
           <Typography variant="body2" >{post.likes.length} likes</Typography>
            <Typography variant="body2" sx={{display:"flex"}}>
              <strong>{post.user.userName}</strong> <span style={{ marginLeft: 8,width:500 }}> {post.description}</span> 
            </Typography>
          </CardContent>
        </Card>
      ))}


    </div>
  );
};

export default Home;
