import './App.css';
import Home from './pages/Home';
import React, { useState } from 'react';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
import Progress from './pages/Progress';
import Leftbar from './component/Leftbar'; // Import the Leftbar component
import Exeplore from './pages/Exeplore';
import Profile from './pages/Profile';
import Messeges from './pages/messeges';
import PostForm from './component/PostForm';
import { createTheme, ThemeProvider } from '@mui/material/styles';


  function App() {
    const theme = createTheme({
      palette: {
        primary: {
          main: '#FFFFFF',
        },
        secondary: {
          main: '#FFFFFF',
        },
      },
    });
    const [posts, setPosts] = useState([]);

    const handlePost = (newPost) => {
      // Update the posts state with the new post
      setPosts([newPost, ...posts]);
    };

  return (
    <ThemeProvider theme={theme}>
      <BrowserRouter className="App">
        <div className="App">
        <Leftbar onPost={handlePost} />
          <Routes>
            <Route path="/Home" element={<Home posts={posts} />} />
            <Route path="/prof" element={<Profile />} />
            <Route path="/prog" element={<Progress />} />
            <Route path="/exp" element={<Exeplore />} />
            <Route path="/Home/" element={<Messeges />} />
          </Routes>
        </div>
      </BrowserRouter>
    </ThemeProvider>
  );
}

export default App;
