import React from 'react';
import { Card, CardContent, Avatar, Typography, IconButton } from '@mui/material';
import FavoriteIcon from '@mui/icons-material/Favorite';
import ChatBubbleOutlineIcon from '@mui/icons-material/ChatBubbleOutline';
import SendIcon from '@mui/icons-material/Send';
import SaveIcon from '@mui/icons-material/Save';
import MoreHorizIcon from '@mui/icons-material/MoreHoriz';
export const defaultPost = {
  username: 'Mohammad',
  caption: 'This is the default post caption.',
  imageUrl: 'https://www.mercedes-amg.com/etc/clientlibs/amg/amg.base/assets/images/svg/amg-logo.svg',
  liked: false,
};
const Post = ({ username, caption, imageUrl, liked, onLike }) => {

  return (
    <Card className="post" sx={{ color: 'white', backgroundColor: 'black' }}>
      <div className="post__header">
        <IconButton className="post__dots" color="primary">
          <MoreHorizIcon />
        </IconButton>
      </div>

      <div className="post__content">
        <div className="post__user-info">
          <Avatar
            className="post__avatar"
            alt={username}
            src="/static/images/avatar/1.jpg" 
          />
          <div className="post__username">
            <Typography variant="subtitle2" color="primary">{username}</Typography>
          </div>
        </div>
        <img className="post__image" src={imageUrl} alt={caption} />
      </div>

      <CardContent>
        <div className="post__buttons">
          <IconButton color={liked ? 'error' : 'primary'} onClick={onLike}>
            <FavoriteIcon />
          </IconButton>
          <IconButton color='primary'>
            <ChatBubbleOutlineIcon />
          </IconButton>
          <IconButton color="primary">
            <SendIcon />
          </IconButton>
          <IconButton color="primary" className="post__save-icon">
            <SaveIcon />
          </IconButton>
        </div>

        {liked ? (
          <Typography variant="body2" color="error">
            1 like
          </Typography>
        ) : null}

        <Typography variant="body2">
          <strong>{username}</strong> {caption}
        </Typography>
      </CardContent>
    </Card>
  );
};

export default Post;
