import React, { useState } from 'react';
import { Container, TextField, Button, Typography } from '@mui/material';
import regimg from "../component/assets/regester.png"
function RegisterPage() {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // In a real application, you would typically send this data to a backend for user registration.
    // For this example, we'll just display the submitted data in the console.
    console.log(formData);
  };

  return (
    <Container maxWidth="sm"  className="regpage" >
      <img src={regimg} alt="" width={200}/>
      <Typography variant="h4" align="center" gutterBottom>
        Register
      </Typography>
      <form onSubmit={handleSubmit} className="regform">
        <TextField
          fullWidth
          label="Username"
          name="username"
          variant="outlined"
          margin="normal"
          value={formData.username}
          onChange={handleChange}
        />
        <TextField
          fullWidth
          label="Email"
          name="email"
          type="email"
          variant="outlined"
          margin="normal"
          value={formData.email}
          onChange={handleChange}
        />
        <TextField
          fullWidth
          label="Password"
          name="password"
          type="password"
          variant="outlined"
          margin="normal"
          value={formData.password}
          onChange={handleChange}
        />
        <Button
          type="submit"
          variant="contained"
          color="primary"
          fullWidth
          size="large"
          sx={{ mt: 2 }}
        >
          Register
        </Button>
      </form>
    </Container>
  );
}

export default RegisterPage;
