// StoryBar.js
import React from 'react';
import { Avatar, Box, CssBaseline, Container } from '@mui/material';

const StoryBar = ({ stories }) => {
  return (
    <Box sx={{ display: 'flex', overflowX: 'auto', p: 1, bgcolor: '#fff', borderBottom: '1px solid #ddd' }}>
      {stories.map((story, index) => (
        <Avatar
          key={index}
          sx={{ width: 60, height: 60, marginRight: 1, cursor: 'pointer', bgcolor: story.unread ? '#ff9800' : '' }}
          alt={`Story ${index + 1}`}
          src={story.imageUrl}
        />
      ))}
    </Box>
  );
};

export default StoryBar;
