import React, { useState } from 'react';
import instalogo from "./assets/instagram-logo.png";
import { Link } from "react-router-dom";
import { Button, Typography } from '@mui/material';

import ListItemButton from '@mui/material/ListItemButton';
import HomeIcon from '@mui/icons-material/Home';
import SearchIcon from '@mui/icons-material/Search';
import ExploreIcon from '@mui/icons-material/Explore';
import SlideshowIcon from '@mui/icons-material/Slideshow';
import ChatIcon from '@mui/icons-material/Chat';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import AddCardIcon from '@mui/icons-material/AddCard';
import MenuIcon from '@mui/icons-material/Menu';
import Avatar from '@mui/material/Avatar';
import boy from '../component/assets/Ezz.png';
import "./components.css";
import PostForm from "./PostForm"; 

function Leftbar({ onPost }) {
  const [showPostForm, setShowPostForm] = useState(false);

  const handlePost = (newPost) => {
    onPost(newPost);
    setShowPostForm(false);
  };
const handleLogout =()=>{
  localStorage.removeItem()
}
  return (
    <div className='leftbar'>
      <img id="insta" src={instalogo} alt="instalogo" width={"100px"} />
      <Link id="but" to="/">
        <ListItemButton>
          <HomeIcon fontSize='small' /> Home
        </ListItemButton>
      </Link>

      <Link id="but" to="/prog">
        <ListItemButton color='white'>
          <SearchIcon fontSize='small' /> Search
        </ListItemButton>
      </Link>

      <Link id="but" to="/exp">
        <ListItemButton>
          <ExploreIcon fontSize='small' /> Explore
        </ListItemButton>
      </Link>

      <Link id="but" to="/prog">
      <ListItemButton>
        <SlideshowIcon fontSize='small' /> Reels
      </ListItemButton>
      </Link>

      <Link id="but" to="/messeges">
      <ListItemButton>
        <ChatIcon fontSize='small' /> Messages
      </ListItemButton>      </Link>

      <Link id="but" to="/prog">
      <ListItemButton>
        <FavoriteBorderIcon fontSize='small' /> Notification
      </ListItemButton>
      </Link>

          <ListItemButton id="but" variant="contained" color="primary" onClick={() => setShowPostForm(!showPostForm)}>
          <AddCardIcon/>
          Create Post
          </ListItemButton>

      {showPostForm && (
        <PostForm onPost={handlePost} />
      )}

      <Link id="but" to="/prof">
        <ListItemButton id="proff">
          <Avatar src={boy} />
          <Typography      sx={{fontSize:"1vw",padding:1,color:"white"}}
>Ezz_Kalash</Typography>        </ListItemButton>
      </Link>
        <Button onClick={handleLogout}>logout</Button>
      <ListItemButton  id="menu">
        <MenuIcon fontSize='small' /> Menu
      </ListItemButton>
    </div>
  );
}

export default Leftbar;
