import React, { useState } from 'react';
import { TextField, TextareaAutosize, Button } from '@mui/material';
import Home from '../pages/Home';

const PostForm = ({ onPost, onCancel }) => {
  const posts = [
    {
      username: 'user1',
      title: 'Title 1',
      caption: 'Caption 1',
      imageUrl: 'https://car-images.bauersecure.com/wp-images/13349/1056x594/80-mercedes-amg-gt63-e-performance-hero.jpg',
    },
    {
      username: 'user2',
      title: 'Title 2',
      caption: 'Caption 2',
      imageUrl: 'https://via.placeholder.com/500x500',
    },
    // Add more post objects here
  ];

  const [likes, setLikes] = useState(Array(posts.length).fill(false));
  const [showPostForm, setShowPostForm] = useState(false); // State to track PostForm visibility

  const toggleLike = (index) => {
    const updatedLikes = [...likes];
    updatedLikes[index] = !updatedLikes[index];
    setLikes(updatedLikes);
  };

  const handleDoubleClick = (index) => {
    const updatedLikes = [...likes];
    updatedLikes[index] = true; // Set liked status to true on double click
    setLikes(updatedLikes);
  };

  const handlePost = (newPost) => {
    // Add the new post to the beginning of the posts array
    posts.unshift(newPost);
    setLikes([false, ...likes]); // Add a like status for the new post
    setShowPostForm(false); // Hide the PostForm after posting
  }
  const [caption, setcaption] = useState('');
  const [body, setBody] = useState('');
  const [url, setUrl] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    // Create a new post object with the entered data
    const newPost = {
      caption,
      body,
      imageUrl: url,
    };

    // Call the onPost function to submit the new post
    onPost(newPost);

    // Clear form fields
    setcaption('');
    setBody('');
    setUrl('');

  };

  return (
    <div>
      <Home></Home>
      <form onSubmit={handleSubmit}>
        <TextField
          label="caption"
          variant="outlined"
          fullWidth
          value={caption}
          onChange={(e) => setcaption(e.target.value)}
        />
        <TextareaAutosize
          placeholder="Post body"
          minRows={3}
          maxRows={5}
          value={body}
          onChange={(e) => setBody(e.target.value)}
        />
        <TextField
          label="Image URL"
          variant="outlined"
          fullWidth
          value={url}
          onChange={(e) => setUrl(e.target.value)}
        />

        <Button type="submit" variant="contained" color="primary">
          Post
        </Button>
        <Button variant="contained" color="secondary" onClick={onCancel}>
          Cancel
        </Button>
      </form>
    </div>
  );
};

export default PostForm;
