import React, { useState } from 'react';
import instalogo from './assets/instagram-logo.png';
import { Link } from 'react-router-dom';
import ListItemButton from '@mui/material/ListItemButton';
import Button from '@mui/material/Button';
import HomeIcon from '@mui/icons-material/Home';
import SearchIcon from '@mui/icons-material/Search';
import ExploreIcon from '@mui/icons-material/Explore';
import SlideshowIcon from '@mui/icons-material/Slideshow';
import ChatIcon from '@mui/icons-material/Chat';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import MenuIcon from '@mui/icons-material/Menu';
import ListItem from '@mui/material/ListItem';
import ListItemText from '@mui/material/ListItemText';
import Avatar from '@mui/material/Avatar';
import boy from '../component/assets/Ezz.png';
import './components.css';

function Leftbar({ onPost }) {
  const [showPostForm, setShowPostForm] = useState(false);
  const [newPost, setNewPost] = useState({
    username: 'user3', // You can set the default username here
    title: '',
    caption: '',
    imageUrl: '',
  });

  const handlePostFormToggle = () => {
    setShowPostForm(!showPostForm);
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setNewPost({ ...newPost, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onPost(newPost);
    // Reset the form
    setNewPost({
      username: 'user3', // Reset username if needed
      title: '',
      caption: '',
      imageUrl: '',
    });
    setShowPostForm(false);
  };

  return (
    <div className="leftbar">
      <img id="insta" src={instalogo} alt="instalogo" width={'100px'} />
      <Link id="but" to="/">
        <ListItemButton>
          <HomeIcon fontSize="small" /> Home
        </ListItemButton>
      </Link>

      {/* Add other links here */}

      <ListItem>
        <ListItemText>
          <Button
            variant="contained"
            color="primary"
            onClick={handlePostFormToggle}
          >
            Create Post
          </Button>
        </ListItemText>
      </ListItem>

      {showPostForm && (
        <div className="post-form">
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              name="title"
              placeholder="Title"
              value={newPost.title}
              onChange={handleInputChange}
            />
            <input
              type="text"
              name="caption"
              placeholder="Caption"
              value={newPost.caption}
              onChange={handleInputChange}
            />
            <input
              type="text"
              name="imageUrl"
              placeholder="Image URL"
              value={newPost.imageUrl}
              onChange={handleInputChange}
            />
            <button type="submit">Post</button>
          </form>
        </div>
      )}

      <Link id="but" to="/prof">
        <ListItemButton id="but">
          <Avatar src={boy} />
          <ListItemText primary="Ezzkalash" secondary="ezzo" />
        </ListItemButton>
      </Link>

      <ListItemButton>
        <MenuIcon fontSize="small" /> Menu
      </ListItemButton>
    </div>
  );
}

export default Leftbar;
