// StoryBar.js
import React from 'react';
import { Avatar, Box } from '@mui/material';


const useStyles = makeStyles((theme) => ({
  storyBar: {
    display: 'flex',
    overflowX: 'auto',
    padding: theme.spacing(1),
    backgroundColor: '#fff',
    borderBottom: `1px solid ${theme.palette.divider}`,
  },
  storyItem: {
    marginRight: theme.spacing(1),
    width: 60,
    height: 60,
    cursor: 'pointer',
  },
  unreadStory: {
    animation: '$pulse 2s infinite',
  },
  '@keyframes pulse': {
    '0%': {
      transform: 'scale(1)',
    },
    '50%': {
      transform: 'scale(1.1)',
    },
    '100%': {
      transform: 'scale(1)',
    },
  },
}));

const StoryBar = ({ stories }) => {
  const classes = useStyles();

  return (
    <Box className={classes.storyBar}>
      {stories.map((story, index) => (
        <Avatar
          key={index}
          className={`${classes.storyItem} ${story.unread ? classes.unreadStory : ''}`}
          alt={`Story ${index + 1}`}
          src={story.imageUrl}
        />
      ))}
    </Box>
  );
};

export default StoryBar;
